import React, { useRef, useState, useEffect } from 'react'
import { Canvas, useFrame, useThree } from '@react-three/fiber'
import { useGLTF, Html } from '@react-three/drei'
import { createRoot } from 'react-dom/client'
import './styles.css'
import { App } from './App'

function Model({ url, position, scale, onClick }) {
  const { scene } = useGLTF(url)
  const ref = useRef()

  useFrame((state, delta) => {
    ref.current.rotation.y += delta * 0.5
  })

  return (
    <primitive 
      ref={ref}
      object={scene} 
      position={position} 
      scale={scale}
      onClick={onClick}
    />
  )
}

function ResponsiveModel({ url, side, onClick, label }) {
  const { viewport } = useThree()
  const [position, setPosition] = useState([0, 0, 0])
  const [scale, setScale] = useState(1)

  useEffect(() => {
    const xPos = side === 'left' ? -viewport.width / 2 + 1 : viewport.width / 2 - 1
    const yPos = -viewport.height / 2 + 1
    setPosition([xPos, yPos, 0])
    setScale(Math.min(viewport.width, viewport.height) * 0.1)
  }, [viewport, side])

  return (
    <>
      <Model 
        url={url} 
        position={position} 
        scale={scale} 
        onClick={onClick}
      />
      <Html position={[position[0], position[1] - 0.5, position[2]]}>
        <div style={{ color: 'white', fontSize: '1rem', textAlign: 'center' }}>{label}</div>
      </Html>
    </>
  )
}

function Root() {
  const openLink = (url) => {
    window.open(url, '_blank')
  }

  const styles = {
    container: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
    },
    title: {
      position: 'absolute',
      top: '20px',
      width: '100%',
      textAlign: 'center',
      fontSize: 'clamp(2rem, 5vw, 4rem)',
      color: 'white',
    },
  }

  return (
    <>
      <App />
      <div style={styles.container}>
        <Canvas camera={{ position: [0, 0, 5] }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />

          <ResponsiveModel 
            url="/pump.glb" 
            side="left"
            onClick={() => openLink("https://pump.fun/")}
            label="PUMP.FUN"
          />

          <ResponsiveModel 
            url="/pump.glb" 
            side="right"
            onClick={() => openLink("https://t.me/SnapshotsInTimePortal")}
            label="Telegram"
          />
        </Canvas>

        <a className='tit press-start-2p-regular' style={styles.title} href="#">
          Snapshots in Time
        </a>
      </div>
    </>
  )
}

createRoot(document.getElementById('root')).render(<Root />)
