import * as THREE from 'three'
import React, { useRef, useState, useEffect, Suspense } from 'react'
import { Canvas, useFrame, useThree } from '@react-three/fiber'
import { Image, Environment, ScrollControls, useScroll, useTexture, useGLTF, Html, useProgress, PerspectiveCamera } from '@react-three/drei'
import { easing } from 'maath'
import './util'
import './App.css'

// Utility functions and custom geometries
class BentPlaneGeometry extends THREE.PlaneGeometry {
  constructor(radius, ...args) {
    super(...args)
    let p = this.parameters
    let hw = p.width * 0.5
    let a = new THREE.Vector2(-hw, 0)
    let b = new THREE.Vector2(0, radius)
    let c = new THREE.Vector2(hw, 0)
    let ab = new THREE.Vector2().subVectors(a, b)
    let bc = new THREE.Vector2().subVectors(b, c)
    let ac = new THREE.Vector2().subVectors(a, c)
    let r = (ab.length() * bc.length() * ac.length()) / (2 * Math.abs(ab.cross(ac)))
    let center = new THREE.Vector2(0, radius - r)
    let baseV = new THREE.Vector2().subVectors(a, center)
    let baseAngle = baseV.angle() - Math.PI * 0.5
    let arc = baseAngle * 2
    let uv = this.attributes.uv
    let pos = this.attributes.position
    let mainV = new THREE.Vector2()
    for (let i = 0; i < uv.count; i++) {
      let uvRatio = 1 - uv.getX(i)
      let y = pos.getY(i)
      mainV.copy(c).rotateAround(center, arc * uvRatio)
      pos.setXYZ(i, mainV.x, y, -mainV.y)
    }
    pos.needsUpdate = true
  }
}

class MeshSineMaterial extends THREE.MeshBasicMaterial {
  constructor(parameters = {}) {
    super(parameters)
    this.setValues(parameters)
    this.time = { value: 0 }
  }
  onBeforeCompile(shader) {
    shader.uniforms.time = this.time
    shader.vertexShader = `
      uniform float time;
      ${shader.vertexShader}
    `
    shader.vertexShader = shader.vertexShader.replace(
      '#include <begin_vertex>',
      `vec3 transformed = vec3(position.x, position.y + sin(time + uv.x * PI * 4.0) / 4.0, position.z);`
    )
  }
}

const CubeLoader = ({ setLoadingComplete }) => {
  const { progress } = useProgress()

  useEffect(() => {
    if (progress === 100) {
      setLoadingComplete(true)
    }
  }, [progress, setLoadingComplete])

  return (
    <Html style={{ zIndex: 10, width: '100vw', height: '100vh' }}>
      <div className="">
        <div className="loader-progress" style={{ width: `${progress}%` }}></div>
      </div>
      <p className="loader-text">The entirety of time is Loading... {progress.toFixed(2)}%</p>
    </Html>
  )
}

function Rig({ fov, children, ...props }) {
  const ref = useRef()
  const scroll = useScroll()
  useFrame((state, delta) => {
    ref.current.rotation.y = -scroll.offset * (Math.PI * 2)
    state.events.update()
    easing.damp3(state.camera.position, [-state.pointer.x * 2, state.pointer.y + 1.5, 10], 0.3, delta)
    state.camera.lookAt(0, 0, 0)
    state.camera.fov = THREE.MathUtils.lerp(state.camera.fov, fov, 0.1)
    state.camera.updateProjectionMatrix()
  })
  return <group ref={ref} {...props}>{children}</group>
}

function Carousel({ radius = 1.4, count = 8 }) {
  return Array.from({ length: count }, (_, i) => (
    <Card
      key={i}
      url={`/img${Math.floor(i % 10) + 1}_.webp`}
      position={[Math.sin((i / count) * Math.PI * 2) * radius, 0, Math.cos((i / count) * Math.PI * 2) * radius]}
      rotation={[0, Math.PI + (i / count) * Math.PI * 2, 0]}
    />
  ))
}

function Card({ url, ...props }) {
  const ref = useRef()
  const [hovered, hover] = useState(false)
  const pointerOver = (e) => (e.stopPropagation(), hover(true))
  const pointerOut = () => hover(false)
  useFrame((state, delta) => {
    easing.damp3(ref.current.scale, hovered ? 1.15 : 1, 0.1, delta)
    easing.damp(ref.current.material, 'radius', hovered ? 0.25 : 0.1, 0.2, delta)
    easing.damp(ref.current.material, 'zoom', hovered ? 1 : 1.5, 0.2, delta)
  })
  return (
    <Image ref={ref} url={url} transparent side={THREE.DoubleSide} onPointerOver={pointerOver} onPointerOut={pointerOut} {...props}>
      <bentPlaneGeometry args={[0.1, 1, 1, 20, 20]} />
    </Image>
  )
}

function Model(props) {
  const { nodes, materials } = useGLTF('/binance.glb')
  const btc = useRef()
  useFrame((state, delta) => {
    btc.current.rotation.y += delta / 0.9
  })
  return (
    <group ref={btc} scale={2} {...props} dispose={null}>
      <mesh castShadow receiveShadow geometry={nodes.mesh.geometry} material={materials.base_material} rotation={[Math.PI / 2, 0, 1.5]} scale={1.707} />
    </group>
  )
}

function Banner(props) {
  const ref = useRef()
  const texture = useTexture('/work_.png')
  texture.wrapS = texture.wrapT = THREE.RepeatWrapping
  const scroll = useScroll()
  useFrame((state, delta) => {
    ref.current.material.time.value += Math.abs(scroll.delta) * 4
    ref.current.material.map.offset.x += delta / 2
  })
  return (
    <mesh ref={ref} {...props}>
      <cylinderGeometry args={[1.6, 1.6, 0.14, 128, 16, true]} />
      <meshSineMaterial map={texture} map-anisotropy={16} map-repeat={[30, 1]} side={THREE.DoubleSide} toneMapped={false} />
    </mesh>
  )
}

function ResponsiveModel({ url, side, onClick, label }) {
  const { viewport } = useThree()
  const [position, setPosition] = useState([0, 0, 0])
  const [scale, setScale] = useState(1)

  useEffect(() => {
    const xPos = side === 'left' ? -viewport.width / 2 + 1 : viewport.width / 2 - 1
    const yPos = -viewport.height / 2 + 1
    setPosition([xPos, yPos, 0])
    setScale(Math.min(viewport.width, viewport.height) * 0.1)
  }, [viewport, side])

  const { scene } = useGLTF(url)
  const ref = useRef()

  useFrame((state, delta) => {
    ref.current.rotation.y += delta * 0.5
  })

  return (
    <>
      <primitive 
        ref={ref}
        object={scene} 
        position={position} 
        scale={scale}
        onClick={(e) => {
          e.stopPropagation()
          onClick()
        }}
      />
      <Html position={[position[0], position[1] - 0.5, position[2]]}>
        <div style={{ color: 'white', fontSize: '1rem', textAlign: 'center', pointerEvents: 'none' }}>{label}</div>
      </Html>
    </>
  )
}

function CameraButton({ onClick }) {
