import React, { useRef } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { useGLTF, Html } from '@react-three/drei'
import { createRoot } from 'react-dom/client'
import './styles.css'
import { App } from './App'

function Model({ url, position, scale, onClick }) {
  const { scene } = useGLTF(url)
  const ref = useRef()

  useFrame((state, delta) => {
    ref.current.rotation.y += delta * 0.5
  })

  return (
    <primitive 
      ref={ref}
      object={scene} 
      position={position} 
      scale={scale}
      onClick={onClick}
    />
  )
}

function Root() {
  const isMobile = window.innerWidth < 768

  const openLink = (url) => {
    window.open(url, '_blank')
  }

  const styles = {
    container: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
    },
    title: {
      position: 'absolute',
      top: isMobile ? '10px' : '20px',
      width: '100%',
      textAlign: 'center',
      fontSize: isMobile ? '2rem' : '4rem',
      color: 'white',
    },
  }

  return (
    <>
      <App />
      <div style={styles.container}>
        <Canvas camera={{ position: [0, 0, 5] }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />

          {/* PUMP.FUN model */}
          <Model 
            url="/pump.glb" 
            position={[-2, 0, 0]} 
            scale={0.5} 
            onClick={() => openLink("https://pump.fun/")}
          />
          <Html position={[-2, -1, 0]}>
            <div style={{ color: 'white', fontSize: isMobile ? '0.8rem' : '1.1rem' }}>PUMP.FUN</div>
          </Html>

          {/* Telegram model */}
          <Model 
            url="/pump.glb" 
            position={[2, 0, 0]} 
            scale={0.5} 
            onClick={() => openLink("https://t.me/SnapshotsInTimePortal")}
          />
          <Html position={[2, -1, 0]}>
            <div style={{ color: 'white', fontSize: isMobile ? '0.8rem' : '1.1rem' }}>Telegram</div>
          </Html>
        </Canvas>

        <a className='tit press-start-2p-regular' style={styles.title} href="#">
          Snapshots in Time
        </a>
      </div>
    </>
  )
}

createRoot(document.getElementById('root')).render(<Root />)
